  {title: "Jurassic World", description: "A new theme park is built on the original site of Jurassic Park. Everything is going well until the park's newest attraction--a genetically modified giant stealth killing machine--escapes containment and goes on a killing spree.", director_first_name: "Colin", director_last_name: "Trevorrow", imdb_id: 369610, year: 2015, release_date: "2015-06-12", mpaa_rating: "PG-13", run_time: 124, user_id: 1, image_file_name: "index.jpg", image_content_type: "image/jpeg", image_file_size: 6247, image_updated_at: "2015-09-07 03:55:05"},
  {title: "Taken 3", description: "Ex-government operative Bryan Mills is accused of a ruthless murder he never committed or witnessed. As he is tracked and pursued, Mills brings out his particular set of skills to find the true killer and clear his name.", director_first_name: "Olivier", director_last_name: "Megaton", imdb_id: 3112344, year: 2015, release_date: "2015-01-09", mpaa_rating: "PG-13", run_time: 109, user_id: 1, image_file_name: "Tak3n_movie_poster.jpg", image_content_type: "image/jpeg", image_file_size: 144404, image_updated_at: "2015-09-12 03:36:53"},
